contracts/CollateralManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

interface IPriceOracle {
    function getPrice(address token) external view returns (uint256);
}

interface IERC20Metadata is IERC20 {
    function decimals() external view returns (uint8);
}

interface ILoanManager {
    function getOutstandingLoanUSD(address user) external view returns (uint256); // ✅ NEW
    function getLoanToken(address user) external view returns (address);          // ✅ NEW
    function repayFor(address borrower, address token, uint256 amount) external;
}

contract CollateralManager is Ownable {
    // ====== STRUCTS ======
    struct CollateralConfig {
        bool allowed;
        uint8 ltv;                  // Loan-to-Value ratio (e.g. 80)
        uint8 liquidationThreshold; // Ngưỡng thanh lý (e.g. 85)
    }

    // ====== STATE ======
    mapping(address => mapping(address => uint256)) public collateralBalances; // user => token => amount
    mapping(address => CollateralConfig) public tokenConfig;                   // token => config
    address[] public allowedTokens;

    IPriceOracle public priceOracle;
    ILoanManager public loanManager;

    // ====== EVENTS ======
    event CollateralDeposited(address indexed user, address indexed token, uint256 amount);
    event CollateralWithdrawn(address indexed user, address indexed token, uint256 amount);
    event CollateralLiquidated(address indexed borrower, address indexed token, address liquidator, uint256 repayAmount, uint256 collateralSeized);

    // ====== CONSTRUCTOR ======
    constructor(address _oracle, address _loanManager, address initialOwner) Ownable(initialOwner) {
        priceOracle = IPriceOracle(_oracle);
        loanManager = ILoanManager(_loanManager);
    }

    // ====== ADMIN ======
    function setLoanManager(address _loanManager) external onlyOwner {
        require(_loanManager != address(0), "Invalid address");
        loanManager = ILoanManager(_loanManager);
    }

    function setPriceOracle(address _oracle) external onlyOwner {
        require(_oracle != address(0), "Invalid oracle");
        priceOracle = IPriceOracle(_oracle);
    }

    function addAllowedToken(address token, uint8 ltv, uint8 liquidationThreshold) external onlyOwner {
        require(!tokenConfig[token].allowed, "Already allowed");
        require(ltv > 0 && liquidationThreshold > ltv, "Invalid config");

        tokenConfig[token] = CollateralConfig({
            allowed: true,
            ltv: ltv,
            liquidationThreshold: liquidationThreshold
        });

        allowedTokens.push(token);
    }

    function updateTokenLTV(address token, uint8 newLtv, uint8 newThreshold) external onlyOwner {
        require(tokenConfig[token].allowed, "Token not allowed");
        require(newLtv > 0 && newThreshold > newLtv, "Invalid LTV");
        tokenConfig[token].ltv = newLtv;
        tokenConfig[token].liquidationThreshold = newThreshold;
    }

    // ====== CORE: DEPOSIT / WITHDRAW ======
    function deposit(address token, uint256 amount) external {
        require(tokenConfig[token].allowed, "Token not allowed");
        require(amount > 0, "Invalid amount");

        IERC20(token).transferFrom(msg.sender, address(this), amount);
        collateralBalances[msg.sender][token] += amount;

        emit CollateralDeposited(msg.sender, token, amount);
    }

    function withdraw(address token, uint256 amount) external {
        require(tokenConfig[token].allowed, "Token not allowed");
        require(amount > 0, "Invalid amount");
        require(collateralBalances[msg.sender][token] >= amount, "Insufficient balance");

        collateralBalances[msg.sender][token] -= amount;

        require(getHealthFactor(msg.sender) >= 1e18, "Cannot withdraw - risk too high");

        IERC20(token).transfer(msg.sender, amount);
        emit CollateralWithdrawn(msg.sender, token, amount);
    }

    /// @notice Rút toàn bộ tài sản thế chấp (khi không còn nợ)
    function withdrawAllCollateral() external {
        uint256 hf = getHealthFactor(msg.sender);
        require(hf >= 1e18, "Risk too high");

        for (uint i = 0; i < allowedTokens.length; i++) {
            address token = allowedTokens[i];
            uint256 balance = collateralBalances[msg.sender][token];
            if (balance == 0) continue;

            collateralBalances[msg.sender][token] = 0;
            IERC20(token).transfer(msg.sender, balance);
            emit CollateralWithdrawn(msg.sender, token, balance);
        }
    }

    // ====== VALUATION ======
    function getCollateralValueUSD(address user) public view returns (uint256 totalValue) {
        for (uint i = 0; i < allowedTokens.length; i++) {
            address token = allowedTokens[i];
            uint256 balance = collateralBalances[user][token];
            if (balance > 0) {
                uint256 price = priceOracle.getPrice(token); // 8 decimals
                uint8 decimals = IERC20Metadata(token).decimals();
                totalValue += (balance * price) / (10 ** decimals);
            }
        }
    }

    function getMaxLoanAllowedUSD(address user) public view returns (uint256 totalUSD) {
        for (uint i = 0; i < allowedTokens.length; i++) {
            address token = allowedTokens[i];
            uint256 balance = collateralBalances[user][token];
            if (balance == 0) continue;

            uint256 price = priceOracle.getPrice(token);
            uint8 decimals = IERC20Metadata(token).decimals();
            uint8 ltv = tokenConfig[token].ltv;

            uint256 valueUSD = (balance * price) / (10 ** decimals);
            totalUSD += (valueUSD * ltv) / 100;
        }
    }

    // ====== HEALTH FACTOR ======
    function getHealthFactor(address user) public view returns (uint256) {
        uint256 debtUSD = loanManager.getOutstandingLoanUSD(user); // ✅ CHANGED
        if (debtUSD == 0) return type(uint256).max;

        uint256 collateralUSD = getCollateralValueUSD(user);
        uint8 weightedThreshold = getWeightedLiquidationThreshold(user);

        return (collateralUSD * weightedThreshold * 1e18) / (debtUSD * 100);
    }

    function getWeightedLiquidationThreshold(address user) internal view returns (uint8) {
        uint256 totalUSD = 0;
        uint256 weightedSum = 0;
        for (uint i = 0; i < allowedTokens.length; i++) {
            address token = allowedTokens[i];
            uint256 bal = collateralBalances[user][token];
            if (bal == 0) continue;
            uint256 price = priceOracle.getPrice(token);
            uint8 decimals = IERC20Metadata(token).decimals();
            uint256 val = (bal * price) / (10 ** decimals);
            totalUSD += val;
            weightedSum += val * tokenConfig[token].liquidationThreshold;
        }
        return totalUSD == 0 ? 0 : uint8(weightedSum / totalUSD);
    }

    // ====== LIQUIDATION ======
    function liquidate(address borrower, address repayToken, uint256 repayAmount, address collateralToken) external {
        require(tokenConfig[collateralToken].allowed, "Collateral not allowed");
        uint256 hf = getHealthFactor(borrower);
        require(hf < 1e18, "Healthy account");

        IERC20(repayToken).transferFrom(msg.sender, address(this), repayAmount);
        IERC20(repayToken).approve(address(loanManager), repayAmount);
        loanManager.repayFor(borrower, repayToken, repayAmount);

        uint256 collateralPrice = priceOracle.getPrice(collateralToken);
        uint8 decimals = IERC20Metadata(collateralToken).decimals();

        uint256 collateralToSeize = (repayAmount * (10 ** decimals) * 105) / (collateralPrice * 100);
        uint256 userBalance = collateralBalances[borrower][collateralToken];
        if (collateralToSeize > userBalance) collateralToSeize = userBalance;

        collateralBalances[borrower][collateralToken] -= collateralToSeize;
        IERC20(collateralToken).transfer(msg.sender, collateralToSeize);

        emit CollateralLiquidated(borrower, collateralToken, msg.sender, repayAmount, collateralToSeize);
    }

    // ====== VIEW HELPERS ======
    function allowedTokensLength() external view returns (uint) {
        return allowedTokens.length;
    }

    function getCollateralValueUSDByToken(address user, address token) external view returns (uint256) {
        uint256 bal = collateralBalances[user][token];
        if (bal == 0) return 0;
        uint256 price = priceOracle.getPrice(token);
        uint8 decimals = IERC20Metadata(token).decimals();
        return (bal * price) / (10 ** decimals);
    }

    /// @notice Dashboard tổng hợp cho UI
    function getUserCollateralData(address user)
        external
        view
        returns (
            uint256 totalCollateralUSD,
            uint256 maxLoanUSD,
            uint256 debtUSD,
            uint256 healthFactor
        )
    {
        totalCollateralUSD = getCollateralValueUSD(user);
        maxLoanUSD = getMaxLoanAllowedUSD(user);
        debtUSD = loanManager.getOutstandingLoanUSD(user); // ✅ CHANGED
        healthFactor = getHealthFactor(user);
    }
}

contracts/LoanManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

interface ICollateralManager {
    function getHealthFactor(address user) external view returns (uint256);
    function getCollateralValueUSD(address user) external view returns (uint256);
}

interface IPriceOracle {
    /// @notice Trả về giá USD 8 decimals cho mỗi 1 token (price * 1e8)
    function getPrice(address token) external view returns (uint256);
}

contract LoanManager is Ownable {
    // ===== DATA =====
    struct Loan {
        address token;        // <— NEW: token đang vay
        uint256 principal;    // gốc đang nợ (token units)
        uint256 rate;         // borrowRate (bps)
        uint256 totalInterest;// tổng lãi đã trả
        uint256 createdAt;    // mốc tính lãi hiện tại
        uint256 duration;     // thời hạn vay (tuỳ UI)
        bool active;
    }

    struct InterestRates {
        uint256 borrowRate; // bps
        uint256 lendRate;   // bps
    }

    struct Lender {
        uint256 deposited;     // gốc đã gửi
        uint256 depositTime;   // mốc tính lãi hiện tại cho phần gửi
        uint256 rewardClaimed; // lãi đã ghi nhận (để trừ khi tính pending)
    }

    struct TreasuryStats {
        uint256 totalDeposits;    // tổng GỐC đã gửi vào pool (kế toán)
        uint256 totalBorrows;     // tổng gốc đã cho vay ra
        uint256 totalRepayments;  // tổng tiền trả nợ đã nhận về
    }

    // ===== STATE =====
    mapping(address => Loan) public loans;                           // borrower => Loan
    mapping(address => InterestRates) public ratesByToken;           // token => rates
    mapping(address => bool) public isSupportedToken;                // token => lending whitelist
    mapping(address => mapping(address => Lender)) public lenders;   // user => token => Lender
    mapping(address => TreasuryStats) public treasury;               // token => stats

    // Liquidator helper
    address[] public activeBorrowers;            // <— NEW
    mapping(address => bool) public isBorrower;  // <— NEW

    ICollateralManager public collateralManager;
    IPriceOracle public priceOracle;             // <— NEW
    IERC20 public rewardToken; // optional: ZAMA

    // ===== EVENTS =====
    event TokenSupported(address token, uint256 borrowRateBps, uint256 lendRateBps);
    event DepositedToPool(address indexed user, address token, uint256 amount);
    event Withdrawn(address indexed user, address token, uint256 principalOut, uint256 interestOut, uint256 totalOut);
    event LoanRequested(address indexed user, address token, uint256 principal, uint256 rateBps);
    event LoanRepaid(address indexed user, address token, uint256 repayAmount, uint256 remainingDebt);
    event LoanRepaidFor(address indexed payer, address indexed borrower, address token, uint256 paid);
    event RewardDistributed(address indexed user, uint256 amount);

    constructor(address _collateralManager, address initialOwner) Ownable(initialOwner) {
        collateralManager = ICollateralManager(_collateralManager);
    }

    // ===== ADMIN =====
    function setPriceOracle(address _oracle) external onlyOwner { // <— NEW
        require(_oracle != address(0), "Invalid oracle");
        priceOracle = IPriceOracle(_oracle);
    }

    function supportToken(address token, uint256 borrowRateBps, uint256 lendRateBps) external onlyOwner {
        require(token != address(0), "Invalid token");
        require(borrowRateBps <= 10000 && lendRateBps <= 10000, "Rate too high");
        ratesByToken[token] = InterestRates(borrowRateBps, lendRateBps);
        isSupportedToken[token] = true;
        emit TokenSupported(token, borrowRateBps, lendRateBps);
    }

    function setRewardToken(address token) external onlyOwner {
        rewardToken = IERC20(token);
    }

    // ===== LENDING (SUPPLY SIDE) =====

    /// @notice Người dùng gửi thanh khoản vào pool
    function depositToPool(address token, uint256 amount) external {
        require(amount > 0, "Amount=0");
        require(isSupportedToken[token], "Unsupported token");

        // checkpoint lãi trước khi cộng thêm gốc mới
        Lender storage l = lenders[msg.sender][token];
        if (l.deposited > 0 && l.depositTime > 0) {
            uint256 accrued = _accrueLender(l.deposited, ratesByToken[token].lendRate, l.depositTime);
            if (accrued > 0) l.rewardClaimed += accrued;
        }

        IERC20(token).transferFrom(msg.sender, address(this), amount);

        l.deposited += amount;
        l.depositTime = block.timestamp;

        treasury[token].totalDeposits += amount;
        emit DepositedToPool(msg.sender, token, amount);
    }

    /// @notice Lãi đang chờ của lender tính từ depositTime
    function calculateLenderReward(address user, address token) public view returns (uint256) {
        Lender memory l = lenders[user][token];
        if (l.deposited == 0 || l.depositTime == 0) return 0;

        uint256 accrued = _accrueLender(l.deposited, ratesByToken[token].lendRate, l.depositTime);
        return accrued > l.rewardClaimed ? (accrued - l.rewardClaimed) : 0;
    }

    /// @notice Rút gốc + lãi một phần (amount <= deposited) hoặc dùng MaxUint để rút all
    function withdraw(address token, uint256 amount) public {
        Lender storage l = lenders[msg.sender][token];
        require(l.deposited > 0, "No deposit");

        uint256 prePrincipal = l.deposited;
        uint256 rewardNow = calculateLenderReward(msg.sender, token);

        if (amount == type(uint256).max) {
            uint256 totalOut = prePrincipal + rewardNow;

            // đảm bảo thanh khoản thực (thay vì chỉ nhìn treasury)
            require(IERC20(token).balanceOf(address(this)) >= totalOut, "Insufficient liquidity");

            // update state
            l.deposited = 0;
            l.depositTime = 0;
            l.rewardClaimed += rewardNow;

            if (treasury[token].totalDeposits >= prePrincipal) {
                treasury[token].totalDeposits -= prePrincipal;
            } else {
                treasury[token].totalDeposits = 0;
            }

            IERC20(token).transfer(msg.sender, totalOut);
            _distributeReward(msg.sender, rewardNow / 10);

            emit Withdrawn(msg.sender, token, prePrincipal, rewardNow, totalOut);
            return;
        }

        require(amount > 0 && amount <= prePrincipal, "Invalid amount");

        // phần lãi trả tỉ lệ theo amount
        uint256 interestOut = prePrincipal > 0 ? (rewardNow * amount) / prePrincipal : 0;
        uint256 totalOutPartial = amount + interestOut;

        require(IERC20(token).balanceOf(address(this)) >= totalOutPartial, "Insufficient liquidity");

        // cập nhật state
        l.deposited = prePrincipal - amount;
        l.rewardClaimed += interestOut;      // ghi nhận phần lãi đã trả
        l.depositTime = block.timestamp;     // checkpoint cho phần còn lại

        if (treasury[token].totalDeposits >= amount) {
            treasury[token].totalDeposits -= amount;
        } else {
            treasury[token].totalDeposits = 0;
        }

        IERC20(token).transfer(msg.sender, totalOutPartial);
        emit Withdrawn(msg.sender, token, amount, interestOut, totalOutPartial);
    }

    /// @notice Rút toàn bộ gốc + lãi (atomic convenience)
    function withdrawAll(address token) external {
        withdraw(token, type(uint256).max);
    }

    // ===== BORROWING (DEBT SIDE) =====

    /// @notice Vay từ pool (đơn token). Yêu cầu có thanh khoản thực tế.
    function requestLoan(address token, uint256 principal, uint256 duration) external {
        require(!loans[msg.sender].active, "Loan active");
        require(principal > 0 && duration > 0, "Invalid input");
        require(isSupportedToken[token], "Unsupported token");

        uint256 rateBps = ratesByToken[token].borrowRate;
        require(rateBps > 0, "Borrow rate not set");

        // tài khoản phải khoẻ trước khi vay (HF >= 1)
        require(collateralManager.getHealthFactor(msg.sender) >= 1e18, "Unhealthy account");

        // phải có thanh khoản thực bằng token
        require(IERC20(token).balanceOf(address(this)) >= principal, "No liquidity");

        loans[msg.sender] = Loan({
            token: token,                 // <— NEW: lưu token đang vay
            principal: principal,
            rate: rateBps,
            totalInterest: 0,
            createdAt: block.timestamp,
            duration: duration,
            active: true
        });

        treasury[token].totalBorrows += principal;

        // track borrower (cho liquidator quét)
        if (!isBorrower[msg.sender]) {
            isBorrower[msg.sender] = true;
            activeBorrowers.push(msg.sender);
        }

        IERC20(token).transfer(msg.sender, principal);
        emit LoanRequested(msg.sender, token, principal, rateBps);
    }

    /// @notice Lãi phải trả tính từ createdAt
    function calculateInterest(address user) public view returns (uint256) {
        Loan memory loan = loans[user];
        if (!loan.active) return 0;
        uint256 elapsed = block.timestamp - loan.createdAt;
        return (loan.principal * loan.rate * elapsed) / (10000 * 365 days);
    }

    /// @notice Người vay tự trả nợ
    function repay(address token, uint256 amount) external {
        // Bảo vệ UI: yêu cầu đúng token đang vay (nếu đã lưu)
        address loanToken = loans[msg.sender].token;
        if (loanToken != address(0)) {
            require(token == loanToken, "Wrong token");
        }
        _repayInternal(msg.sender, msg.sender, token, amount);
    }

    /// @notice Trả thay (liquidator / bot / bạn bè trả hộ)
    function repayFor(address borrower, address token, uint256 amount) external {
        require(borrower != address(0), "Invalid borrower");
        address loanToken = loans[borrower].token;
        if (loanToken != address(0)) {
            require(token == loanToken, "Wrong token");
        }
        _repayInternal(msg.sender, borrower, token, amount);
        emit LoanRepaidFor(msg.sender, borrower, token, amount);
    }

    /// @notice Trả toàn bộ nợ (gốc + lãi) trong 1 tx
    function repayAll(address token) external {
        Loan memory loan = loans[msg.sender];
        require(loan.active, "No active loan");
        if (loan.token != address(0)) {
            require(token == loan.token, "Wrong token");
        }
        uint256 totalDebt = loan.principal + calculateInterest(msg.sender);
        _repayInternal(msg.sender, msg.sender, token, totalDebt);
    }

    function _repayInternal(address payer, address borrower, address token, uint256 amount) internal {
        Loan storage loan = loans[borrower];
        require(loan.active, "No active loan");
        require(amount > 0, "Amount=0");

        IERC20(token).transferFrom(payer, address(this), amount);
        treasury[token].totalRepayments += amount;

        uint256 interest = calculateInterest(borrower);
        uint256 totalDebt = loan.principal + interest;
        if (amount > totalDebt) amount = totalDebt;

        uint256 remaining = amount;

        // 1) trả lãi trước
        if (remaining >= interest) {
            remaining -= interest;
            loan.totalInterest += interest;
        } else {
            loan.totalInterest += remaining;
            remaining = 0;
        }

        // 2) trừ gốc
        if (remaining > 0) {
            if (remaining >= loan.principal) {
                loan.principal = 0;
            } else {
                loan.principal -= remaining;
            }
        }

        // 3) cập nhật mốc tính lãi
        loan.createdAt = block.timestamp;

        // 4) đóng loan nếu hết nợ
        if (loan.principal == 0) {
            loan.active = false;
            // chỉ thưởng nếu tự trả (tránh confusion khi repayFor)
            if (payer == borrower) {
                _distributeReward(borrower, interest / 10);
            }
        }

        uint256 remainingDebt = loan.active ? (loan.principal + calculateInterest(borrower)) : 0;
        emit LoanRepaid(borrower, token, amount, remainingDebt);
    }

    /// @notice Nợ hiện tại (gốc + lãi) theo đơn vị token gốc
    function getOutstandingLoan(address user) external view returns (uint256) {
        Loan memory loan = loans[user];
        if (!loan.active) return 0;
        return loan.principal + calculateInterest(user);
    }

    /// @notice NEW: Nợ quy USD (8 decimals) = (debtRaw * priceUSD8) / 10**decimals(token)
    function getOutstandingLoanUSD(address user) external view returns (uint256) { // <— NEW
        Loan memory loan = loans[user];
        if (!loan.active) return 0;
        require(address(priceOracle) != address(0), "Oracle not set");
        require(loan.token != address(0), "No loan token");
        uint256 debtRaw = loan.principal + calculateInterest(user);

        uint256 px = priceOracle.getPrice(loan.token); // 8 decimals
        uint8 dec = IERC20Metadata(loan.token).decimals();
        return (debtRaw * px) / (10 ** dec);
    }

    /// @notice NEW: trả về token đang vay (address(0) nếu chưa)
    function getLoanToken(address user) external view returns (address) { // <— NEW
        return loans[user].token;
    }

    /// @notice Thanh khoản thực có thể chi trả (debug/UI)
    function getAvailableLiquidity(address token) external view returns (uint256) {
        return IERC20(token).balanceOf(address(this));
    }

    // ===== LIQUIDATOR HELPERS (Phase 2) =====
    function getActiveBorrowers() external view returns (address[] memory) { // <— NEW
        return activeBorrowers;
    }

    function getActiveBorrowerCount() external view returns (uint256) { // <— NEW
        return activeBorrowers.length;
    }

    // ===== INTERNAL =====
    function _accrueLender(uint256 principal, uint256 lendRateBps, uint256 fromTs) internal view returns (uint256) {
        uint256 elapsed = block.timestamp - fromTs;
        return (principal * lendRateBps * elapsed) / (10000 * 365 days);
    }

    function _distributeReward(address user, uint256 amount) internal {
        if (address(rewardToken) != address(0) && amount > 0) {
            uint256 bal = rewardToken.balanceOf(address(this));
            if (bal >= amount) {
                rewardToken.transfer(user, amount);
                emit RewardDistributed(user, amount);
            }
        }
    }
}

contracts/ManualPriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title ManualPriceOracle
 * @dev Stores USD prices (8 decimals) for supported tokens. USDC is hard-coded
 *      to $1.00, other tokens must be updated by the owner (e.g., via off-chain bot).
 */
contract ManualPriceOracle is Ownable {
    struct PriceData {
        uint256 price;      // USD price with 8 decimals
        uint256 updatedAt;  // last update timestamp
    }

    event PriceUpdated(address indexed token, uint256 price, uint256 timestamp);

    uint256 public constant MAX_STALE_TIME = 10 minutes;
    address public immutable usdc;

    mapping(address => PriceData) private prices;

    constructor(address _usdc, address initialOwner) Ownable(initialOwner) {
        require(_usdc != address(0), "ManualPriceOracle: invalid USDC");
        usdc = _usdc;
    }

    function setPrice(address token, uint256 price) external onlyOwner {
        require(token != address(0), "ManualPriceOracle: invalid token");
        require(token != usdc, "ManualPriceOracle: USDC immutable");
        _setPrice(token, price);
    }

    function setPrices(address[] calldata tokens, uint256[] calldata priceList) external onlyOwner {
        require(tokens.length == priceList.length, "ManualPriceOracle: length mismatch");
        for (uint256 i = 0; i < tokens.length; i++) {
            require(tokens[i] != address(0), "ManualPriceOracle: invalid token");
            require(tokens[i] != usdc, "ManualPriceOracle: USDC immutable");
            _setPrice(tokens[i], priceList[i]);
        }
    }

    function getPrice(address token) external view returns (uint256) {
        if (token == usdc) {
            return 1e8;
        }
        PriceData memory data = prices[token];
        require(data.price > 0, "ManualPriceOracle: price not set");
        require(block.timestamp - data.updatedAt <= MAX_STALE_TIME, "ManualPriceOracle: price stale");
        return data.price;
    }

    function _setPrice(address token, uint256 price) internal {
        require(price > 0, "ManualPriceOracle: invalid price");
        prices[token] = PriceData({ price: price, updatedAt: block.timestamp });
        emit PriceUpdated(token, price, block.timestamp);
    }
}

contracts/MonadNativeFaucet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title MonadNativeFaucet
 * @notice Faucet contract for native MON tokens on Monad testnet.
 *         - Anyone can deposit MON into this contract.
 *         - Anyone can claim once per cooldown.
 *         - Owner can configure faucet amount, cooldown, and withdraw.
 */
contract MonadNativeFaucet {
    address public owner;
    uint256 public faucetAmount = 0.5 ether; // 0.5 MON
    uint256 public cooldown = type(uint256).max; // Claim only once

    mapping(address => uint256) public lastClaimed;

    event Deposited(address indexed sender, uint256 amount);
    event Claimed(address indexed user, uint256 amount);
    event Withdrawn(address indexed to, uint256 amount);

    constructor() {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    // ✅ Ai cũng có thể gửi MON vào faucet
    receive() external payable {
        emit Deposited(msg.sender, msg.value);
    }

    /// @notice Claim MON (1 lần mỗi cooldown)
    function claim() external {
        _claim(msg.sender);
    }

    /// @notice Owner gọi claim cho user (dùng cho API faucet)
    function claimFor(address user) external onlyOwner {
        _claim(user);
    }

    /// @dev Internal claim logic
    function _claim(address recipient) internal {
        require(address(this).balance >= faucetAmount, "Faucet empty");
        
        // Cho phép claim nếu chưa từng claim (lastClaimed == 0) 
        // HOẶC đã qua đủ thời gian cooldown
        require(
            lastClaimed[recipient] == 0 || block.timestamp - lastClaimed[recipient] >= cooldown,
            "Please wait before next claim"
        );

        lastClaimed[recipient] = block.timestamp;

        (bool ok, ) = recipient.call{value: faucetAmount}("");
        require(ok, "Transfer failed");

        emit Claimed(recipient, faucetAmount);
    }

    /// @notice Owner có thể đổi lượng phát hoặc cooldown
    function setFaucetAmount(uint256 _amount) external onlyOwner {
        faucetAmount = _amount;
    }

    function setCooldown(uint256 _seconds) external onlyOwner {
        cooldown = _seconds;
    }

    /// @notice Cho phép owner rút MON nếu cần
    function withdraw(uint256 _amount) external onlyOwner {
        require(_amount <= address(this).balance, "Insufficient balance");
        (bool ok, ) = owner.call{value: _amount}("");
        require(ok, "Withdraw failed");
        emit Withdrawn(owner, _amount);
    }

    /// @notice Xem số dư faucet hiện tại
    function faucetBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

